<template>
  <div class="home px-12 pt-10">
    <h1 class=" font-bold pb-4 text-leclercBlue text-2xl">Mes listes</h1>
    <div class=" bg-gray-300 h-24 mb-8">
       <i data-feather="shopping-cart" class="h-24 w-24 pb-8 inline-block"></i>
       <h3 class="inline-block  pl-8 pt-2 text-2xl">Vacances <br> ~35min</h3> 
       <div class="inline-block float-right pr-8 pt-5">
       <SimpleButton name="Editer" class="w-48 inline-block float-right text-xl" style></SimpleButton> 
       </div>
    </div>
    <div class="  bg-gray-300 h-24">
       <i data-feather="shopping-cart" class="h-24 w-24 pb-8 inline-block"></i>
       <h3 class="inline-block  pl-8 pt-2 text-2xl">Hebdomadaires <br> ~25min</h3> 
       <div class="inline-block float-right pr-8 pt-5">
       <SimpleButton name="Editer" class="w-48 inline-block float-right text-xl" style></SimpleButton> 
       </div>
    </div>


    <div class="w-full flex justify-between mt-24">
       <SimpleButton name="Ajouter une liste" class="w-5/12 inline-block float-right text-3xl" style></SimpleButton> 
       <SimpleButton name="Scanner la liste" class="w-5/12 inline-block float-right text-3xl" style></SimpleButton> 
    </div>

    <div class=" bg-gray-300 my-20">
      <Carousel
        @next="next"
        @prev="prev"
        >
        
        <CarouselSlide v-for="(slide,index) in slides" 
        :key="slide" 
        :index="index"
        :visibleSlide="visibleSlide" class="pl-64"
        >
          <p class="inline-block">rdddddlllllllllllllllllllllllllddddrr</p><img :src="slide" class="inline-block"/>
        </CarouselSlide>
      </Carousel>
    </div>

  </div>
</template>

<script>
import SimpleButton from "../components/SimpleButton.vue"
import Carousel from "../components/Carousel.vue"
import CarouselSlide from "../components/CarouselSlide.vue"
import feather from "feather-icons"
export default {

  name: 'Home',
  data() {
    return {
      slides: [
        'https://courses.monoprix.fr/images-v2/0c44253f-c4a3-4340-9d37-d41e42b9d14a/235c7f87-0a09-4b04-be29-80e27ef55216/98c23728-d857-48c2-ac46-cde5d82f23c9/7a865ec4-df7f-4a81-bcd7-5447639e593a/300x300.jpg',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'https://www.google.com/imgres?imgurl=https%3A%2F%2Fwww.fleurymichon.fr%2Fsites%2Fdefault%2Ffiles%2Fstyles%2Fproduct%2Fpublic%2Fproduct%2Fimage%2F3095758355103_0.png%3Fitok%3D0xq2RDph&imgrefurl=https%3A%2F%2Fwww.fleurymichon.fr%2Fproduit%2Fjambon-le-tranche-fin-litalienne&tbnid=riUeeIP9Pj4MnM&vet=12ahUKEwiHtrrazrHsAhUP9BoKHZk1AMYQMygXegUIARD6AQ..i&docid=oLJ-NoWVCQ5THM&w=416&h=400&q=jambon%20image&ved=2ahUKEwiHtrrazrHsAhUP9BoKHZk1AMYQMygXegUIARD6AQ',
      ],
      visibleSlide: 0,
    }
  },

  computed: {
    slidesLen() {
      return this.slides.length;
    }
  },
  methods : {
    next() {
      if(this.visibleSlide >= this.slidesLen -1) {
        this.visibleSlide = 0;
      }else {
        this.visibleSlide++;
      }
    },
    prev(){
      if(this.visibleSlide <= 0) {
        this.visibleSlide = this.slidesLen -1;
      }else {
        this.visibleSlide--;
      }
    }
  },
  components: {
    SimpleButton,
    Carousel,
    CarouselSlide,
  },

  mounted(){
    feather.replace()
  },

  
}
</script>
